class KernelCFG(object):

    def __init__(self):
        self.configs=subprocess.check_output('find -name "Kconfig*" -exec grep "^config [A-Z]" {} \;',shell=True)
        self.configs=self.configs.replace('config ','CONFIG_').split('\n')
    
    def filter_cfgs(self):
        return filter(lambda x:self.mark in x,self.configs)
   
    @property 
    def get_supported_cfgs(self):
        return filter(lambda x: x not in self.blacklist,self.filter_cfgs())
        
    
